div.script:
(function(){
    var searchApp = document.querySelector('.SearchApp[data-script-id]');

    var d = searchApp.dataset;

    var id = d.scriptId;
    var srcM = d.scriptMSrc;
    var src = d.scriptSrc;
    var srcW = d.scriptWSrc;

    delete d.scriptId;
    delete d.scriptMSrc;
    delete d.scriptSrc;
    delete d.scriptWSrc;

    function $loadjs(src,fallback,id,s) {
        s = document.createElement('script')
        s.async = true
        if ('noModule' in s) s.type = 'module', s.src = src
        else s.src = fallback
        if (id) s.id = id
        document.head.appendChild(s)
    }

    var input = searchApp.querySelector('input');

    function handleFocus(e) {
        input.removeEventListener('focus', handleFocus, false);

        var searchScript = document.getElementById(id);
        if (searchScript) {
            return;
        }

        $loadjs(srcM, src, id);
    }

    input.addEventListener('focus', handleFocus, false);
})();

header.script:
(function(){
    var sidebar = document.getElementById('sidebar');
    var backdrop = document.querySelector('#sidebar + .Sidebar-Backdrop');
    var toggleButtons = document.querySelectorAll('[data-action="sidebar-toggle"]');
    var closeButtons = document.querySelectorAll('[data-action="sidebar-close"]');
    function handleOpen() {
        sidebar.classList.add('Sidebar_open');
        document.body.classList.add('is-showing-sidebar');
    }
    function handleClose() {
        sidebar.classList.remove('Sidebar_open');
        document.body.classList.remove('is-showing-sidebar');
    }
    function handleToggle() {
        if (sidebar.classList.contains('Sidebar_open')) {
            handleClose();
        } else {
            handleOpen();
        }
    }
    var i;
    for (i = 0; i < toggleButtons.length; i++) {
        toggleButtons[i].addEventListener('click', handleToggle, false);
    }
    for (i = 0; i < closeButtons.length; i++) {
        closeButtons[i].addEventListener('click', handleClose, false);
    }
    if (backdrop) {
        backdrop.addEventListener('click', function(e) {
            if (sidebar.classList.contains('Sidebar_open')) {
                e.preventDefault();
                e.stopPropagation();
                handleClose();
            }
        }, false);
    }
})();

div.script:
(function(){
    var deparam = (function(d,x,params,pair,i) {
        return function (qs) {
            params = {};
            qs = qs.substring(qs.indexOf('?')+1).replace(x,' ').split('&');
            for (i = qs.length; i > 0;) {
                pair = qs[--i].split('=');
                params[d(pair[0])] = d(pair[1]);
            }
            return params;
        };
    })(decodeURIComponent, /\+/g);
    var forEach = Array.prototype.forEach;

    function show(popupButton, withoutHistory) {
        var bodyWidthStart = document.body.clientWidth;
        document.body.classList.add('modal_open');
        var bodyWidthEnd = document.body.clientWidth;

        if (bodyWidthEnd - bodyWidthStart) {
            document.body.style.setProperty('--modal-open-padding', (bodyWidthEnd - bodyWidthStart) + 'px');
        }

        var dataset = popupButton.dataset;
        var template = document.querySelector(dataset.useTemplate + '[data-template]').cloneNode(true);
        delete template.dataset.template;

        forEach.call(template.querySelectorAll('[data-t]'), function(el) {
            forEach.call(el.dataset.t.split(','), function(replace) {
                replace = replace.split(':');
                var att = replace[0];
                var d = replace[1] || replace[0];
                if (dataset[d]) {
                    el.setAttribute(att, dataset[d]);
                } else {
                    console.warn('Template replace not found', dataset, replace);
                }
            });
        });

        forEach.call(template.querySelectorAll('[data-ts]'), function(el) {
            forEach.call(el.dataset.ts.split(','), function(replace) {
                replace = replace.split(':');
                var att = replace[0];
                var d = replace[1] || replace[0];
                if (dataset[d]) {
                    el.style[att] = dataset[d];
                } else {
                    console.warn('Template replace style not found', dataset, replace);
                }
            });
        });

        forEach.call(template.querySelectorAll('[data-td]'), function(el) {
            el.parentNode.removeChild(el);
        });	

        var modals = document.getElementById('modals');
        if (! modals) {
            modals = document.createElement('div');
            modals.id = 'modals';
            document.body.appendChild(modals);
        }
        var modal = document.createElement('div');
        modal.classList.add('modal');
        var closeButton = document.createElement('button');
        closeButton.classList.add('modal-close');
        closeButton.insertAdjacentHTML('afterbegin', '');
        template.appendChild(closeButton);
        modal.appendChild(template);
        modals.appendChild(modal);

        function handleClick(e) {
            if (e.target === modal) {
                close();
            }
        }

        function handleEsc(e) {
            if (e.keyCode === 27 && modals.lastChild === modal) {
                e.stopPropagation();
                e.preventDefault();
                close();
            }
        }

        function handlePopState(e) {
            if (modals.lastChild === modal) {
                modal.removeEventListener('click', handleClick);
                window.removeEventListener('keydown', handleEsc);
                if (! withoutHistory) {
                    window.removeEventListener('popstate', handlePopState);
                }
                closeButton.removeEventListener('click', close);
                modals.removeChild(modal);
                if (! modals.childNodes.length) {
                    document.body.classList.remove('modal_open');
                    document.body.style.removeProperty('--modal-open-padding');
                }
            }
        }

        function close() {
            if (withoutHistory) {
                handlePopState();
            } else {
                history.back();
            }
        }

        modal.addEventListener('click', handleClick);
        closeButton.addEventListener('click', close);
        window.addEventListener('keydown', handleEsc);

        if (! withoutHistory) {
            window.addEventListener('popstate', handlePopState);

            history.pushState({ popup: true }, '', '?logo=' + encodeURIComponent(dataset.qs) );
        }
    }

    var params = deparam(location.search);
    if (params && params.logo) {
        var buttonEl = document.querySelector('[data-popup-button][data-qs-name="logo"][data-qs="' + params.logo + '"]');
        if (buttonEl) {
            show(buttonEl, true);
        }
    }

    forEach.call(document.querySelectorAll('[data-popup-button]'), function(popupButton) {
        popupButton.addEventListener('click', function(e) {
            show(popupButton);
        });
    });
})();

body.script:
(function() {
    function $loadjs(src,c,s) {
        s = document.createElement('script')
        s.async = true
        s.src = src
        c && c(s)
        document.head.appendChild(s)
    }
    function m() {
        window.removeEventListener('click', m);
        window.removeEventListener('touchstart', m);
        window.removeEventListener('scroll', m);
        setTimeout(function() {
            window.dataLayer = window.dataLayer || [];
            function gtag(){dataLayer.push(arguments);}
            gtag('js', new Date());

            gtag('config', 'UA-152740724-1', { 'anonymize_ip': true });

            $loadjs("https://www.googletagmanager.com/gtag/js?id=UA-152740724-1");
        }, 100);
    }

    window.addEventListener('DOMContentLoaded', function() {
        window.addEventListener('click', m);
        window.addEventListener('touchstart', m);
        window.addEventListener('scroll', m);
    });
})();
